Script.Inputs = {
    SqlData: "object",
}

Script.Outputs = {
    Output: "array",
    LatCenter: "number",
    LongCenter: "number",
    MapZoon: "number",
    Done: "signal"
}

Script.Signals.Run = function () {
// function calculateAverage(array) {
//     var total = 0;
//     var count = 0;
//     array.forEach(function(item, index) {
//         total += item;
//         count++;
//     });
//     return total / count;
// }



    var geoArray = []
    var sqlData = {...Script.Inputs.SqlData}
    var count = 0
    for (const site in sqlData){
        // set path pref
        if(sqlData[site]["path_data"] != null){
            if(sqlData[site]["path_data"]["vsat_sel"]){var selPath = 'VSAT'};
            if(sqlData[site]["path_data"]['cell_sel']){var selPath = 'Cellular'};
            if(sqlData[site]["path_data"]['aux_sel']){var selPath = 'Auxillary'};
        }
        else{selPath = 'Unavailable'};


        // deterime if data is damaged
        if(sqlData[site]["path_data"] != null){
            if (sqlData[site]["path_data"]["vsat_sel"] != null && sqlData[site]["path_data"]["vsat_path_pref"] != null &&
            sqlData[site]["path_data"]["cell_sel"] != null && sqlData[site]["path_data"]["cell_path_pref"] != null &&
            sqlData[site]["path_data"]["aux_sel"] != null && sqlData[site]["path_data"]["aux_path_pref"] != null){
                if(sqlData[site]["path_data"]["vsat_sel"] != sqlData[site]["path_data"]["vsat_path_pref"] || 
                sqlData[site]["path_data"]["cell_sel"] != sqlData[site]["path_data"]["cell_path_pref"] ||
                sqlData[site]["path_data"]["aux_sel"] != sqlData[site]["path_data"]["aux_path_pref"]){
                var color = "#edb200";
                count = count + 1
                }
                else{
                    var color = "green"
                }
            }
            else {
                var color = "grey"
            }
        }
        else{
            var color = "#ad0000" 
        }
        // set color
    
        if(sqlData[site]["path_data"] != null){
            if(sqlData[site]["path_data"]["vsat_sel"] != sqlData[site]["path_data"]["vsat_path_pref"] || 
                sqlData[site]["path_data"]["cell_sel"] != sqlData[site]["path_data"]["cell_path_pref"] ||
                sqlData[site]["path_data"]["aux_sel"] != sqlData[site]["path_data"]["aux_path_pref"]){
                var color = "#edb200";
                count = count + 1
            }
            else{
                var color = "green"
            }
        }
        else{var color = "#ad0000"};

        // push
        geoArray.push({"lat": sqlData[site]["latitude"], "long": sqlData[site]["longitude"], 
                        "common_name": sqlData[site]["common_name"], "site_id": sqlData[site]["site_id"], "sel_path": selPath,
                        "color": color})
    };
    // Calculate average radius
    var lats = []
    var longs = []
    var maxlat = -90;
    var minlat = 90;
    var maxlong = -180;
    var minlong = 180;
    for (const site in sqlData){
        lats.push(sqlData[site]["latitude"])
        longs.push(sqlData[site]["longitude"])

        if (sqlData[site]["latitude"] > maxlat){maxlat = sqlData[site]["latitude"]};
        if (sqlData[site]["latitude"] < minlat){minlat = sqlData[site]["latitude"]};
        if (sqlData[site]["longitude"] > maxlong){maxlong = sqlData[site]["longitude"]}
        if (sqlData[site]["longitude"] < minlong){minlong = sqlData[site]["longitude"]}
    }
    // 69 miles per degree lat, 55 miles per deree long
    var avgLat = Math.max(...lats) - ((Math.max(...lats) - Math.min(...lats)) / 2)
    var avgLong = Math.max(...longs) - ((Math.max(...longs) - Math.min(...longs)) / 2)

    // Get distance between min max points
    function getDistanceFromLatLonInKm(lat1, lon1, lat2, lon2) {
        var R = 6371; // Radius of the earth in km
        var dLat = deg2rad(lat2-lat1);  // deg2rad below
        var dLon = deg2rad(lon2-lon1); 
        var a = 
            Math.sin(dLat/2) * Math.sin(dLat/2) +
            Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) * 
            Math.sin(dLon/2) * Math.sin(dLon/2)
            ; 
        var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
        var d = R * c; // Distance in km
        return d;
        }

        function deg2rad(deg) {
        return deg * (Math.PI/180)
    }

    var siteDistance = getDistanceFromLatLonInKm(minlat, minlong, maxlat, maxlong) * 1.5
    // if center of map position is between 25 and 35 degrees of latitude use this map for sizing.
    if (avgLat < 30){
        var latMap = {1380: 4, 2070: 3.5, 2760: 3, 4140: 2.5, 5520: 2, 8280: 1.5, 11040: 1, 16560: 0.5};
        var done = false;
        var mapZoom = 0;
        for (const dis in latMap){
            if (siteDistance < dis && !done){
                mapZoom = latMap[dis];
                done = true;
            };
        };
    };
    // if center of map position is between 30 and 50 degrees of latitude use this map for sizing.
    if (avgLat >= 30 && avgLat < 50){
        var latMap = {1125: 4, 1688: 3.5, 2250: 3, 3375: 2.5, 4500: 2, 6750: 1.5, 9000: 1, 13500: 0.5};
        var done = false;
        var mapZoom = 0;
        for (const dis in latMap){
            if (siteDistance < dis && !done){
                mapZoom = latMap[dis];
                done = true;
            };
        };
    };
    // if center of map position is between 50 and 50 degrees of latitude use this map for sizing.
    if (avgLat >= 50 && avgLat < 70){
        var latMap = {733: 4, 1100: 3.5, 1466: 3, 2199: 2.5, 2932: 2, 4398: 1.5, 5864: 1, 8796: 0.5};
        var done = false;
        var mapZoom = 0;
        for (const dis in latMap){
            if (siteDistance < dis && !done){
                mapZoom = latMap[dis];
                done = true;
            };
        };
    };
    
    Script.Outputs.MapZoon = mapZoom;
    Script.Outputs.LatCenter = avgLat;
    Script.Outputs.LongCenter = avgLong;
    Script.Outputs.Output = geoArray;
    Script.Outputs.Done();
};

