Script.Signals.Run = function () {
    var outputData = []

    // Extract needed values
    var inputData = Script.Inputs.InputData
    for (const site in inputData){
        outputTemp = {}
        for (const kpi in inputData[site]) {
            outputTemp[kpi] = inputData[site][kpi]
        }
        outputData.push(outputTemp)
    }
    
    var kpiData = outputData
    var pathData = Script.Inputs.Paths
    for (const site in kpiData){
        for (const x in pathData) {
            if (kpiData[site]["site_id"] == pathData[x]["site_id"]){
                kpiData[site]["path_data"] = {
                "vsat_sel": pathData[x]["vsat_sel"],
                "cell_sel": pathData[x]["cell_sel"],
                "aux_sel": pathData[x]["aux_sel"],
                "v_status_mean": pathData[x]["v_status_mean"],
                "c_status_mean": pathData[x]["c_status_mean"],
                "a_status_mean": pathData[x]["a_status_mean"],
                "v_sla_pass_last": pathData[x]["v_sla_pass_last"],
                "c_sla_pass_last": pathData[x]["c_sla_pass_last"],
                "a_sla_pass_last": pathData[x]["a_sla_pass_last"],
                "vsat_path_pref": pathData[x]["vsat_path_pref"],
                "cell_path_pref": pathData[x]["cell_path_pref"],
                "aux_path_pref": pathData[x]["aux_path_pref"]};
            }
        }
    }
    Script.Outputs.ParsedOutput = kpiData;
    Script.Outputs.Done();

}

