Script.Signals.Run = function (){
    var data = Script.Inputs.Data[0]['_value']
    // if less than a GB show as MB;
    if (data < 1000){
        var outputData = data.toFixed(0)
        var unitType = "MB"
    }
    else{
        var outputData = (data /  1000).toFixed(1)
        var unitType = "GB"
        }
    Script.Outputs.Usage = outputData;
    Script.Outputs.Unit = unitType;
    Script.Outputs.Mounted = true;
};
