function sorting(){
    var kpiData =Script.Inputs.KpiData
    function compare(a, b) {
        if(Script.Inputs.SortAsc != null && Script.Inputs.SortAsc){var ascDesc = 1}else{var ascDesc = -1};
        if(Script.Inputs.SortCol != null){var col = Script.Inputs.SortCol}else{var col = "site_id"};
        const itemA = a[col].toUpperCase();
        const itemB = b[col].toUpperCase();
        if (itemA < itemB) {
            comparison = 1;
        } else if (itemA > itemB) {
            comparison = -1;
        }
        return comparison * ascDesc;
    };


    if(kpiData != null && Script.Inputs.SortCol != null && Script.Inputs.SortAsc != null){kpiData.sort(compare)}
    Script.Outputs.KpiOutput = kpiData;
    Script.Outputs.Loaded();
};

