                    filteredSiteIdArray.push({"field": "site_id", "value": siteIdArray[siteId]["value"]})
                }
            }
        }
        
        if (filteredSiteIdArray != null){Script.Outputs.FilteredSiteIds = filteredSiteIdArray}
        else{Script.Outputs.FilteredSiteIds = siteIdArray};
        Script.Outputs.Done();
    }
}

Script.Signals.Run = function () {
    onChange()
};

Script.Setters.SqlData = onChange;

