function filter() {

    // add if ticket has been read
    var data = Script.Inputs.Data
    for (const tkt in data){
        if(Script.Inputs.ReadTkts.includes(data[tkt]['id'])){data[tkt]['read'] = true}else{data[tkt]['read'] = false};
    }

    var filteredData = []
    var temp = []
    var siteIdFilter = Script.Inputs.SiteIDFilter
    // Filter By Site ID
    if (siteIdFilter == "All"){siteIdFilter = null}
    if (siteIdFilter != null){
        for (const tkt in data){
            if(data[tkt]['site_id'].toLowerCase().startsWith(Script.Inputs.SiteIDFilter.toLowerCase())){
                temp.push(data[tkt])
            };
        };
    }
    else{
        temp = data
    };
    filteredData = temp;
    temp = []
    
    var priorityFilter = Script.Inputs.PriorityFilter
    if (priorityFilter == "All"){priorityFilter = null}
    if (priorityFilter != null){
        for (const tkt in filteredData){
            if(filteredData[tkt]['priority'] == priorityFilter){
                temp.push(filteredData[tkt])
            }
        }
    }    
    else {
        temp = filteredData
    };
    filteredData = temp;
    temp =[]

    var statusFilter = Script.Inputs.StatusFilter
    if (statusFilter == "All"){statusFilter = null}
    if (statusFilter != null){
        for (const tkt in filteredData){
            if(statusFilter == "Closed" && filteredData[tkt]['closed_date'] != null){
                temp.push(filteredData[tkt])   
            }
            else if(statusFilter == "Resolved" && filteredData[tkt]['resolved_date'] != null && filteredData[tkt]['closed_date'] == null){
                temp.push(filteredData[tkt])
            }
            else if (statusFilter == "Open" && filteredData[tkt]['resolved_date'] == null && filteredData[tkt]['closed_date'] == null){
                temp.push(filteredData[tkt])
            }
            else if (statusFilter == "New" && filteredData[tkt]['read'] == false && filteredData[tkt]['resolved_date'] == null && filteredData[tkt]['closed_date'] == null){
                temp.push(filteredData[tkt])
            }
        }
    }
    else {
        temp = filteredData
    }
    filteredData = temp;


    Script.Outputs.FilteredTkts = filteredData
    Script.Outputs.Done();
};

