// ---
// Advanced Code
const windowKey = '__' + uniqueId + '_onload';
function injectScript(onload) {
    if (document.querySelectorAll('#' + uniqueId).length === 0) {
        window[windowKey] = [onload]

        var script = document.createElement('script');
        script.id = uniqueId;
        script.setAttribute('src', scriptUrl);
        script.onload = () => {
            window[windowKey].forEach(cb => cb())
            window[windowKey] = []
        }
        document.head.appendChild(script)
    } else {
        if (window[windowKey].length > 0) {
            window[windowKey].push(onload)
        } else {
            onload()
        }
    }
}

