Script.Outputs = {
    HtmlContent: 'string',
    SaveContent: 'signal',
    Done: "signal"
};

// To make sure that the app is performant after long use,
// we will save this current editor and delete it once this
// node is not active anymore in the visual tree.
let currentEditor = null;



Script.Signals.Setup = function () {
    // Get the DOM element from the Noodl Node connected from the "This" input.
    const element = Script.Inputs.This.getDOMElement();
    element.addEventListener("focus", (event) => {
    });
    // Initialize the TinyMCE editor. 
    tinymce.init({
        height: "200px",
        target: element,
        resize: false,
        // Lets remove the resize grip, and let Noodl handle the size
        menubar: false,
        setup: function (editor) {
            currentEditor = editor;
            // Listen to all the change events
            // https://www.tiny.cloud/docs/tinymce/6/events/#supported-browser-native-events
            editor.on('init', function () {
                if (Script.Inputs.Content) {
                    // Set the default value here
                    editor.setContent(Script.Inputs.Content);
                    Script.Outputs.HtmlContent = editor.getContent();
                }
            });

            function onChange() {
                // Output the current HTML Content
                console.log('changing')
                Script.Outputs.HtmlContent = editor.getContent();

                // Send a signal that the content have changed
                Script.Outputs.Changed();
            }

            // Fired when the editor content changes have been committed,
            // such as when moving focus away from the editor.
            // editor.on('change', function (e) {
            //     onChange();
            // });

            editor.on('keyup', function (e) {
                onChange();
            });
        },
        plugins: ['link', 'lists'],
        toolbar: 'bold italic underline strikethrough forecolor backcolor link | alignleft aligncenter alignright alignjustify | indent outdent | bullist numlist'
    });
    Script.Outputs.Done();
};

// To avoid any odd UX, we only want to update the Content on demand,
// So to update the content after the initial content, we have to call this signal.
Script.Signals.UpdateContent = function () {
    if (!currentEditor) return;

    const newContent = Script.Inputs.Content || '';
    currentEditor.setContent(newContent);
};

// called when the node is unmounted
Script.OnDestroy = function () {
    if (!currentEditor) return;
    currentEditor.destroy();
    currentEditor = null;
}


Script.Signals.ResetContent = function() {
    currentEditor.setContent('')
}

// called when the node is unmounted
Script.Signals.Destory = function () {
    if (!currentEditor) return;
   // Script.Outputs.SaveContent();
    currentEditor.setContent('')
    //currentEditor.setContent('');
    currentEditor.destroy();
    currentEditor = null;
};

Script.Signals.Closed = function () {
    Script.Outputs.HtmlContent = '';
    currentEditor.setContent('');
    currentEditor.destroy();

};

