    ReplyBody: "string"
}

Script.Outputs = {
    ReplyOut: "string"
}

function onChange() {
    var body = Script.Inputs.ReplyBody
    const result = body.replace(/#([^#]+)#/g, (match, key) => {
        // If there's a replacement for the key, return that replacement with a `<br />`. Otherwise, return a empty string.
        return new Date(key * 1000).toLocaleString('en-US', { timeZoneName: 'short' })
    });
    Script.Outputs.ReplyOut = result
}

