Script.Inputs = {
    KpiData: "object",
    SortCol: "string",
    SortAsc: "boolean"
};

Script.Outputs = {
    KpiOutput: "object"
}



function onChange (){
    var rawData = Script.Inputs.KpiData
    var kpiData = []
    for (const site in rawData){
        temp = {};
        for (const kpi in rawData[site]){
            temp[kpi] = rawData[site][kpi]
        }
    kpiData.push(temp)
    }

    function compare(a, b) {
        if(Script.Inputs.SortAsc != null && Script.Inputs.SortAsc){var ascDesc = 1}else{var ascDesc = -1};
        if(Script.Inputs.SortCol != null){var col = Script.Inputs.SortCol}else{var col = "site_id"};

        if (["site_id", "common_name"].includes(col)) {var itemA = a[col].toUpperCase()}else{var itemA = a[col]};
        if (["site_id", "common_name"].includes(col)) {var itemB = b[col].toUpperCase()}else{var itemB = b[col]};
        if (itemA < itemB) {
            comparison = 1;
        } else if (itemA > itemB) {
            comparison = -1;
        }
        return comparison * ascDesc;
    };

    if(kpiData != null && Script.Inputs.SortCol != null && Script.Inputs.SortAsc != null){
        kpiData.sort(compare)}
    Script.Outputs.KpiOutput = kpiData;
};

Script.Setters.KpiData = onChange;
Script.Setters.SortAsc = onChange;
Script.Setters.SortCol = onChange;

