function onChange() {
    if (Script.Inputs.Filter != null && Script.Inputs.KpiData != null){
    var rawData = Script.Inputs.KpiData
    var kpiData = []
    for (const site in rawData){
        temp = {};
        for (const kpi in rawData[site]){
            temp[kpi] = rawData[site][kpi]
        }
    kpiData.push(temp)
    }
    filter = Script.Inputs.Filter;
    var filteredStationCfg = kpiData.filter(station => {
        if(station.site_id != null){var siteId = station.site_id.toLowerCase()};
        if(station.common_name != null){var commonName = station.common_name.toLowerCase()};
        if(filter != null){var searchInput = filter.toLowerCase()};
        return siteId.startsWith(searchInput) || commonName.startsWith(searchInput);});
    }
    Script.Outputs.DataOutputFiltered = filteredStationCfg;
    Script.Outputs.Loaded();
    
};

